mapper.insert:
insert into ${jobLockTable} (id, lock_id, lock_node, 
  lock_expire_time, priority, create_time, update_time
  )
values (#{id,jdbcType=BIGINT}, #{lockId,jdbcType=VARCHAR}, #{lockNode,jdbcType=VARCHAR}, 
  #{lockExpireTime,jdbcType=TIMESTAMP}, #{priority,jdbcType=INTEGER}, #{createTime,jdbcType=BIGINT}, #{updateTime,jdbcType=BIGINT}
  )

mapper.update:
update ${jobLockTable}
set lock_node = #{lockNode,jdbcType=VARCHAR},

  lock_expire_time = TIMESTAMPADD(SECOND,#{renewalSec,jdbcType=BIGINT},CURRENT_TIMESTAMP),


  lock_expire_time = CURRENT_TIMESTAMP + interval '${renewalSec} second',

update_time = #{updateTime,jdbcType=BIGINT}
where lock_id = #{lockId,jdbcType=VARCHAR}
and (lock_node is null or lock_expire_time < CURRENT_TIMESTAMP or lock_node =
#{lockNode,jdbcType=VARCHAR})

mapper.delete:
delete from ${jobLockTable}
where lock_id = #{lockId,jdbcType=VARCHAR}

  AND lock_node = #{lockNode,jdbcType=VARCHAR}

mapper.select:
SELECT lock_id, priority
FROM ${jobLockTable}
WHERE lock_node IS NULL OR lock_expire_time < CURRENT_TIMESTAMP
ORDER BY priority ASC

  LIMIT #{batchSize,jdbcType=INTEGER}

SELECT count(1)
FROM ${jobLockTable}
WHERE lock_id != 'master_scheduler' and lock_expire_time >= CURRENT_TIMESTAMP

SELECT lock_id
FROM ${jobLockTable}
WHERE lock_id != 'master_scheduler' and (lock_expire_time < CURRENT_TIMESTAMP OR lock_node IS NULL)
ORDER BY create_time

  LIMIT #{batchSize,jdbcType=INTEGER}
